import React from "react";
import axios from "axios";
//import sweetalert from "sweetalert";
import swal from "sweetalert";
import { useForm } from "react-hook-form";
//import { useEffect, usestate } from "react";
import "./styles.css";
import "bootstrap/dist/css/bootstrap.min.css";
import se from "./se.js";
import { useEffect } from "react";
import img_50 from "./images/img_50.jpg";

function SignUp() {
  useEffect(() => {
    se();
  }, []);
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();

  //console.log(errors);
  const onSubmit = (data) => {
    console.log(data);

    axios
      .post("http://localhost:8080/login/register", data)
      .then((response) => {
        //swal(response.data);
        console.log(response.data);
        window.location.href = "/signin";
      })
      .catch((error) => {
        swal("something went wrong");
      });
  };

  // const wait = (ms) => new Promise((resolve) => setTimeout(resolve, ms));
  // const emailIsUnique = async (email) => {
  //   await wait(1000);
  //   return email !== "someone@somewhere.com";
  // };

  return (
   // <div className="App">
   <div
   className="container-fluid "
   style={{
     backgroundImage: `url(${img_50})`,
     backgroundPosition: "center",
     backgroundSize: "cover",
     backgroundRepeat: "no-repeat",
     height: "1400px",
   }}
 >
   <div
     className="container py-5"
     style={{ width: "40vw", backgroundColor: "rgba(0,0,0,0.4)" }}
   >
     
      <div className="container py-5">
        <h1>Registration Form</h1>
       {/* <div className="card border-0 shadow w-75 p-5 mx-auto">*/}
          <form onSubmit={handleSubmit(onSubmit)}>

            <div className="form-group my-4 text-center mt-3 text-light">
              <label htmlFor="email">UserName</label>
              <input
                type="text"
                className="form-control"
                id="email"
                name="email"
                placeholder="Enter Your UserName"
                {...register("email", {
                  required: true
                  // ,
                  //validate: emailIsUnique,
                  // pattern: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                })}
              />
              {errors.email && errors.email.type === "required" && (
                <span role="alert" class="imp">
                  *This field is required
                </span>
              )}
              {errors.email && errors.email.type === "validate" && (
                <div className="error" class="imp">
                  This email address already exists
                </div>
              )}
              {errors.email && errors.email.type === "pattern" && (
                <div className="error" class="imp">
                  *Invalid email address
                </div>
              )}
            </div>
            <div className="form-group my-4 text-center mt-3 text-light">
              <label htmlFor="password">password</label>
              <input
                type="text"
                className="form-control"
                id="password"
                name="password"
                placeholder="Enter Your password"
                {...register("password", {
                  required: true
                  // ,
                  // validate: confirm,
                  // pattern: {
                  //   value:
                  //     /^(?=.*[A-Za-z])(?=.*\d)(?=.*[@$!%*#?&])[A-Za-z\d@$!%*#?&]{8,}$/,
                  // },
                })}
              />
              {errors.password && errors.password.type === "required" && (
                <span role="alert" class="imp">
                  *This field is required
                </span>
              )}
              {errors.password && errors.password.type === "pattern" && (
                <span role="alert" class="imp">
                  *Must Contain 8 Characters, One Uppercase, One Lowercase, One
                  Number and one special case Character
                </span>
              )}
            </div>
            

            <button className="btn btn-success" type="submit">
              Create New Account
            </button>
            <br />
            <br></br>

            <a href="signin">Existing User? Login</a>
          </form>
        </div>
      </div>
    </div>
  );
}

export default SignUp;
