import { useEffect, useState } from "react";
import axios from "axios";
import swal from "sweetalert";
import use from "./use.js";

export default function ViewList() {
  let [userList, setUserList] = useState([]);

  useEffect(() => {
    use();
    readAllUser();
  }, []);

  function deletecomp(id) {
    deleteTask(id);
  }
  const readAllUser = async () => {
    console.log(sessionStorage.getItem("user"));
    axios
      .post(
        "http://localhost:8080/todo/viewTasks",
        JSON.parse(sessionStorage.user).userId
      )
      .then((response) => {
        console.log(response);
        setUserList(response.data.reverse());
      })
      .catch((error) => {
        swal("something went wrong");
      });
  };

  function deleteTask(TaskId) {
    console.log(TaskId);
    axios
      .post("http://localhost:8080/todo/DeleteTask", TaskId)
      .then((response) => {
        window.location = "/user/viewList";
        swal("Task Deleted!!");
      })
      .catch((error) => {
        swal("something went wrong");
      });
  }
  

  const logout = () => {
    sessionStorage.clear();
    window.location = "/";
  };

  return (
    <div style={{ height: "max-content" }}>
      <h1 className="bg-dark text-light p-3" style={{ height: "auto" }}>
        Todo Task List{" "}
      </h1>
      <div className="text-center mt-0">
      <a href="/user/MakeList">
        <button className="btn btn-info p-2 my-1">Make List</button>
      </a>
      </div>
      <div className="container">
        {userList.length>0 ? userList.map((item) => {
          return (
            <div
              className=" border border-dark m-3 "
              style={{ backgroundColor: "#f1f1f1" }}
            >
              <span className="m-1 p-2">
                <b>UserId:</b> {item.userId}
              </span>
              <span className="m-1 p-2">
                <b>Date: </b>
                {item.date}
              </span>
              
              <br />
              <hr />
              <div className="m-1 p-2">
                <h6>
                  <b>Description :</b>
                </h6>
                {item.description}
              </div>
              <hr />
              <span className="m-2 p-2 ">
                <b>Task Id:  </b>
                {item.taskId}
              </span>
              <br />
              <div className="my-4 mx-2 ">
                <button
                  className="btn btn-danger"
                  onClick={
                    () => deletecomp(item.taskId)
                  }
                >
                  delete
                </button>
              </div>
             
            </div>
          );
        }): <div className="text-center" style={{fontSize: 30}}><b>No Task to do please make a task</b> </div>} 
      </div>
    </div>
  );
}
