import React, { useEffect } from "react";
import axios from "axios";
import Swal from "sweetalert2";
import { Link } from "react-router-dom";
import { useForm } from "react-hook-form";
import "./styles.css";
import "bootstrap/dist/css/bootstrap.min.css";
import se from "./se.js";
import img_50 from "./images/img_50.jpg";

function SignIn() {
  useEffect(() => {
    se();
  }, []);

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();

  
  const onSubmit = (data) => {
    console.log(data);
    axios
      .post("http://localhost:8080/login/login", data)
      .then((response) => {
        
        sessionStorage.setItem("user", JSON.stringify(response.data.user));
          console.log(JSON.parse(sessionStorage.user).userId);
          window.location.href = "/user/viewList";
        
       
      })
      .catch((error) => {
        console.log(error.response.data.result);
        Swal.fire(error.response.data.result);
        
      });
  };

  return (
   
      <div
      className="container-fluid "
      style={{
        backgroundImage: `url(${img_50})`,
        backgroundPosition: "center",
        backgroundSize: "cover",
        backgroundRepeat: "no-repeat",
        height: "1000px",
      }}
    >
      <div
        className="container py-5"
        style={{ width: "40vw", backgroundColor: "rgba(0,0,0,0.4)" }}
      >
        <div className="alert  alert-light  bg-dark  text-center mt-5 h2 text-light"> 
          <b> Login Here</b>
        </div>
     
          <div>
            <form onSubmit={handleSubmit(onSubmit)}>
              <div className="form-group my-4 text-center mt-3 text-light" >
                <label htmlFor="email">UserName </label>
                <input
                  type="text"
                  className="form-control"
                  id="email"
                  name="email"
                  placeholder="Enter Your UserName"
                  {...register("email", {
                    required: true,
                   
                  })}
                />
                {errors.email && errors.email.type === "required" && (
                  <span role="alert" class="imp">
                    *This field is required
                  </span>
                )}
                {errors.email && errors.email.type === "validate" && (
                  <div className="error" class="imp">
                    This email address already exists
                  </div>
                )}
                {errors.email && errors.email.type === "pattern" && (
                  <div className="error" class="imp">
                    *Invalid email address
                  </div>
                )}
              </div>

              <div className="form-group my-4 text-center mt-3 text-light">
                <label htmlFor="password">password</label>
                <input
                  type="text"
                  className="form-control"
                  id="password"
                  name="password"
                  placeholder="Enter Your password"
                  {...register("password", {
                    required: true
                  })}
                />
                {errors.password && errors.password.type === "required" && (
                  <span role="alert" class="imp">
                    *This field is required
                  </span>
                )}
               
              </div>

              <button className="btn btn-primary">Login</button>
              <br />
              <p className="forgot-password " color="red">
                <Link to={"/forgot"}>Forgot Password?</Link>
              </p>

              <p className=" Register " color="red">
                <Link to={"/signup"}>New User?</Link>
              </p>
            </form>
          </div>
        </div>
      </div>
    
  );
}

export default SignIn;
