import React from "react";
import axios from "axios";
import swal from "sweetalert";
import { useForm } from "react-hook-form";
import "./styles.css";
import "bootstrap/dist/css/bootstrap.min.css";
import se from "./se.js";
import { useEffect } from "react";
import img_50 from "./images/img_50.jpg";

function SignUp() {
  useEffect(() => {
    se();
  }, []);
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();

  const onSubmit = (data) => {
    console.log(data);

    axios
      .post("http://localhost:8080/login/register", data)
      .then((response) => {
      
        console.log(response.data);
        window.location.href = "/signin";
      })
      .catch((error) => {
        swal("something went wrong");
      });
  };

  return (
   <div
   className="container-fluid "
   style={{
     backgroundImage: `url(${img_50})`,
     backgroundPosition: "center",
     backgroundSize: "cover",
     backgroundRepeat: "no-repeat",
     height: "1400px",
   }}
 >
   <div
     className="container py-5"
     style={{ width: "40vw", backgroundColor: "rgba(0,0,0,0.4)" }}
   >
     
      <div className="container py-5">
        <h1>Registration Form</h1>
          <form onSubmit={handleSubmit(onSubmit)}>

            <div className="form-group my-4 text-center mt-3 text-light">
              <label htmlFor="email">UserName</label>
              <input
                type="text"
                className="form-control"
                id="email"
                name="email"
                placeholder="Enter Your UserName"
                {...register("email", {
                  required: true
                 
                })}
              />
              {errors.email && errors.email.type === "required" && (
                <span role="alert" class="imp">
                  *This field is required
                </span>
              )}
              {errors.email && errors.email.type === "validate" && (
                <div className="error" class="imp">
                  This email address already exists
                </div>
              )}
              {errors.email && errors.email.type === "pattern" && (
                <div className="error" class="imp">
                  *Invalid email address
                </div>
              )}
            </div>
            <div className="form-group my-4 text-center mt-3 text-light">
              <label htmlFor="password">password</label>
              <input
                type="text"
                className="form-control"
                id="password"
                name="password"
                placeholder="Enter Your password"
                {...register("password", {
                  required: true
                 
                })}
              />
              {errors.password && errors.password.type === "required" && (
                <span role="alert" class="imp">
                  *This field is required
                </span>
              )}
              {errors.password && errors.password.type === "pattern" && (
                <span role="alert" class="imp">
                  *Must Contain 8 Characters, One Uppercase, One Lowercase, One
                  Number and one special case Character
                </span>
              )}
            </div>
            

            <button className="btn btn-success" type="submit">
              Create New Account
            </button>
            <br />
            <br></br>

            <a href="signin">Existing User? Login</a>
          </form>
        </div>
      </div>
    </div>
  );
}

export default SignUp;
